import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Table, Row, Col } from 'react-bootstrap';

const Dashboard = () => {
  const token = localStorage.getItem("token");
  const [users, setUsers] = useState([]);
  const [userRole, setUserRole] = useState(null); // New state to track user role
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetching user details and role from the backend
        const response = await fetch("http://localhost:5000/api/users/me", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const result = await response.json();

        if (response.ok) {
          setUserRole(result.role);  // Set role for user or admin
        } else {
          console.error("Error fetching user data:", result.message);
        }

        // If admin, fetch the list of users
        if (result.role === "admin") {
          const usersResponse = await fetch("http://localhost:5000/api/users", {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          const usersResult = await usersResponse.json();
          setUsers(usersResult);  // Set the list of users for admin
        }
      } catch (error) {
        console.log("Error during data fetching:", error);
      }
    };

    if (token) {
      fetchData();
    } else {
      navigate("/login");
    }
  }, [token, navigate]);

  return (
    <div className="mt-5">
      <Row>
        <Col>
          {userRole === "admin" ? (
            <>
              <h1 className="text-center">Admin Dashboard</h1>
              <Table striped bordered hover responsive>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                  </tr>
                </thead>
                <tbody>
                  {Array.isArray(users) && users.length > 0 ? (
                    users.map((user) => (
                      <tr key={user._id}>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="2">No users found</td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </>
          ) : (
            <h1 className="text-center">Welcome, {userRole === "user" ? "User" : "Guest"}!</h1>
          )}
        </Col>
      </Row>
    </div>
  );
};

export default Dashboard;
