import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Table, Row, Col, Button, Image } from 'react-bootstrap';

const Dashboard = () => {
  const token = localStorage.getItem("token");
  const [user, setUser] = useState({ name: '', profilePicture: '' });
  const [cryptos, setCryptos] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const navigate = useNavigate();

  // Fetch user data (name and profile picture) from token
  useEffect(() => {
    const fetchUserData = async () => {
      if (token) {
        try {
          const decodedToken = JSON.parse(atob(token.split('.')[1]));
          setUser({
            name: decodedToken.name,
            profilePicture: decodedToken.profilePicture || 'https://via.placeholder.com/100'
          });

          // Fetch cryptocurrency prices
          const response = await fetch("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd");
          const data = await response.json();
          setCryptos(data);
        } catch (error) {
          console.error("Error fetching user data or cryptocurrency data:", error);
        }
      } else {
        navigate("/login");
      }
    };

    fetchUserData();
  }, [token, navigate]);

  // Handle adding a cryptocurrency to favorites
  const handleAddFavorite = (crypto) => {
    if (!favorites.some((fav) => fav.id === crypto.id)) {
      setFavorites([...favorites, crypto]);
    }
  };

  // Handle removing a cryptocurrency from favorites
  const handleRemoveFavorite = (cryptoId) => {
    setFavorites(favorites.filter((fav) => fav.id !== cryptoId));
  };

  return (
    <div className="dashboard-container">
      <Row>
        <Col xs={12} sm={6} md={4} className="user-info">
          <div className="profile-info">
            <Image src={user.profilePicture} roundedCircle />
            <h3>{user.name}</h3>
          </div>
        </Col>

        <Col xs={12} sm={6} md={8}>
          <h1>Cryptocurrency Prices</h1>
          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Name</th>
                <th>Symbol</th>
                <th>Price (USD)</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {cryptos.map((crypto) => (
                <tr key={crypto.id}>
                  <td>{crypto.name}</td>
                  <td>{crypto.symbol}</td>
                  <td>${crypto.current_price.toLocaleString()}</td>
                  <td>
                    {!favorites.some((fav) => fav.id === crypto.id) ? (
                      <Button variant="success" onClick={() => handleAddFavorite(crypto)}>
                        Add to Favorites
                      </Button>
                    ) : (
                      <Button variant="danger" onClick={() => handleRemoveFavorite(crypto.id)}>
                        Remove from Favorites
                      </Button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          <h2>My Favorite Cryptos</h2>
          {favorites.length === 0 ? (
            <p>No favorite cryptocurrencies added.</p>
          ) : (
            <Table striped bordered hover responsive>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Symbol</th>
                  <th>Price (USD)</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {favorites.map((fav) => (
                  <tr key={fav.id}>
                    <td>{fav.name}</td>
                    <td>{fav.symbol}</td>
                    <td>${fav.current_price.toLocaleString()}</td>
                    <td>
                      <Button variant="danger" onClick={() => handleRemoveFavorite(fav.id)}>
                        Remove from Favorites
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Col>
      </Row>
    </div>
  );
};

export default Dashboard;
